  </div>
  <!-- Repositories View Section -->
  <div class="mx-16">
    <ul role="list" class="divide-y divide-[#E5E4E2]">
      <li *ngFor="let repo of repoData">
        <app-repoview [data]="repo" />
      </li>
    </ul>
  </div>
  <!-- Pagination Section -->
  <div
    class="flex items-center justify-between border-t border-[#E5E4E2] py-3 mx-16 relative"
  >
    <div class="hidden sm:flex sm:flex-1 items-center sm:justify-between">

      <!-- Page Navigation Panel Display -->
      <div>
        <nav
          class="isolate inline-flex -space-x-px rounded-md shadow-sm"
          aria-label="Pagination"

        >
          <div
            (click)="previousPage()"
            class="relative inline-flex items-center rounded-l-md px-2 py-2 text-[#E5E4E2] ring-1 ring-inset ring-gray-300 hover:bg-[#0e4166] hover:text-[#E5E4E2] focus:z-20 focus:outline-offset-0"
          >
            <span class="sr-only">Previous</span>
            <!--Hidden from user, shown to screen readers-->
            <svg
              class="h-5 w-5"
              viewBox="0 0 20 20"
              fill="currentColor"
              aria-hidden="true"
            >
              <path
                fill-rule="evenodd"
                d="M12.79 5.23a.75.75 0 01-.02 1.06L8.832 10l3.938 3.71a.75.75 0 11-1.04 1.08l-4.5-4.25a.75.75 0 010-1.08l4.5-4.25a.75.75 0 011.06.02z"
                clip-rule="evenodd"
              />
            </svg>
          </div>
          <div
            *ngFor="let page of displayedPages"
            (click)="setPage(page)"
            [class]="currentPage === page ? 'bg-purple-600' : ''"
            class="relative inline-flex items-center px-4 py-2 text-sm font-semibold text-white ring-1 ring-inset ring-gray-300 hover:text-[#ffcc00] hover:bg-[#0e4166] focus:z-20 focus:outline-offset-0"
          >
            {{ page === -1 ? "..." : page }}
          </div>

          <div
            (click)="nextPage()"
            class="relative inline-flex items-center rounded-r-md px-2 py-2 text-gray-400 ring-1 ring-inset ring-gray-300 hover:bg-[#0e4166] hover:text-[#E5E4E2] focus:z-20 focus:outline-offset-0"
          >
            <span class="sr-only">Next</span>
            <svg
              class="h-5 w-5"
              viewBox="0 0 20 20"
              fill="currentColor"
              aria-hidden="true"
            >
              <path
                fill-rule="evenodd"
                d="M7.21 14.77a.75.75 0 01.02-1.06L11.168 10 7.23 6.29a.75.75 0 111.04-1.08l4.5 4.25a.75.75 0 010 1.08l-4.5 4.25a.75.75 0 01-1.06-.02z"
                clip-rule="evenodd"
              />
            </svg>
          </div>
        </nav>
      </div>
    </div>
  </div>
</div>
