import { Component, Input } from '@angular/core';
import { ApiService } from 'src/app/services/api.service';

@Component({
  selector: 'app-repoview',
  templateUrl: './repoview.component.html',
  styleUrls: ['./repoview.component.scss'],
})
export class RepoviewComponent {
  constructor(private apiService: ApiService) {}

  // User Data from Profile Component
  @Input() data: any;

  /**
   * @description Returns Elapsed time from updated_at timestamp
   * @param updatedAt
   * @returns Time difference
   */
  getElapsedTime(updatedAt: string) {
    var currentDate = new Date();

    return this.timeDifference(currentDate.toString(), updatedAt);
  }

  /**
   * @description Time difference string
   * @param current
   * @param previous
   * @returns string
   */
  timeDifference(current: string, previous: string) {
    var msPerMinute = 60 * 1000;
    var msPerHour = msPerMinute * 60;
    var msPerDay = msPerHour * 24;
    var msPerMonth = msPerDay * 30;
    var msPerYear = msPerDay * 365;

    var currentDate = Date.parse(current);
    var previousDate = Date.parse(previous);

    var elapsed = currentDate - previousDate;

    if (elapsed < msPerMinute) {
      return Math.round(elapsed / 1000) + ' seconds ago';
    } else if (elapsed < msPerHour) {
      return Math.round(elapsed / msPerMinute) + ' minutes ago';
    } else if (elapsed < msPerDay) {
      return Math.round(elapsed / msPerHour) + ' hours ago';
    } else if (elapsed < msPerMonth) {
      return Math.round(elapsed / msPerDay) + ' days ago';
    } else if (elapsed < msPerYear) {
      return Math.round(elapsed / msPerMonth) + ' months ago';
    } else {
      return Math.round(elapsed / msPerYear) + ' years ago';
    }
  }
}
<!-- Repo View Component -->
<div class="flex flex-col w-full gap-2 my-5">
  <!-- Repo Name -->
  <div class="w-full flex gap-2 items-center leading-7 [&:not(:first-child)]:mt-6" id="repo-name">
    <a [href]="data?.html_url" class="scroll-m-20 text-xl font-semibold tracking-tight text-[#E5E4E2]">{{
      data?.name
    }}</a>
    <div
      class="rounded-[2rem] my-0 h-fit font-medium border-slate-600 text-slate-400 text-xs px-2 py-0 border"
    >
      {{ data?.private ? "Private" : "Public" }}
    </div>
  </div>
  <!-- Repo Description -->
  <div class="flex justify-between w-full items-end">
    <div class="flex flex-col gap-2 w-4/5">
      <p class="text-sm font-medium leading-none text-[#E5E4E2]">
        {{ data?.description !== null ? data?.description : "No Description" }}
      </p>
      <!-- Tags Display from Tag Data-->
      <div class="flex flex-wrap gap-2 items-center">
        <button
          class="bg-purple-500 bg-opacity-10 text-[#E5E4E2] rounded-lg px-4 py-1.5"
          *ngFor="let topic of data?.topics"
        >
          {{ topic }}
        </button>
        <!--Stars-->
        <div class="text-[#E5E4E2] flex gap-1 items-center justify-center">
          ✩
          {{ data?.stargazers_count }}
        </div>
      </div>
    </div>
    <!-- Last Commit Details -->
    <div class="text-end w-1/5">
      <div class=" text-sm text-muted-foreground min-w-[150px] text-[#E5E4E2] mt-5 text-end">
        Last commit&nbsp;<time datetime="2023-01-23T13:23Z">{{
          getElapsedTime(data?.updated_at)
        }}</time>
      </div>
    </div>
  </div>
</div>
