import { Component } from '@angular/core';
import { ApiService } from 'src/app/services/api.service';
import { ToastrService } from 'ngx-toastr';

export interface ProfileData {
  avatar_url: string;
  name: string;
  bio: string;
  location: string;
  twitter_username: string;
  html_url: string;
};

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
})
export class SearchComponent {
  constructor(private apiService: ApiService, private toastr: ToastrService) {}

  githubUsername = '';
  public profileData: ProfileData; // Profile Data from API
  public showProfile: boolean = false;
  public loading: boolean = false;

  /**
   * @description Call Github API for getting github user data
   */
  searchGithubUsers() {
    // Toastr for showcasing warnings
    if (this.githubUsername === '') {
      this.toastr.warning('Please enter a username!', 'Warning');
    } else {
      this.loading = true;
      this.apiService.getUser(this.githubUsername)?.subscribe(
        (res: ProfileData) => {
          this.profileData = res;
          setTimeout(() => {
            this.loading = false;
            this.showProfile = true;
          }, 1000);
        },
        (error) => {
          this.loading = false;
          this.toastr.error(
            'Unable to fetch user from Github!',
            'Error Message'
          );
        }
      );
    }
  }
}
<!-- Search Repo Component -->
<div
  class="mx-2.5 relative w-screen overflow-x-hidden min-h-screen h-screen z-10 "
>
  <div class="mx-auto px-12 flex flex-col w-full justify-center items-center">
    <div class="mx-auto px-16 flex w-full lg:max-w-none justify-between">
      <div class="max-w-xl lg:max-w-lg">

        <!-- Description -->
        <!-- <p class="leading-7 [&:not(:first-child)]:mt-6 text-[#E5E4E2]">
          A place where you can find all the existing public github repositories
          without even getting logged in.
        </p> -->
        <!-- Input Box for Search -->
        <div class="mt-3 flex max-w-md gap-x-4">
          <label for="username" class="sr-only text-[#F8F8FF]">Username</label> 

          <input
            id="username"
            name="username"
            [(ngModel)]="githubUsername"
            type="text"
            autocomplete="username"
            required
            class="min-w-0 flex-auto rounded-md border-0 bg-white/5 px-3.5 py-2 text-white shadow-sm ring-1 ring-inset ring-white/10 focus:ring-2 focus:ring-inset focus:ring-indigo-500 sm:text-sm sm:leading-6"
            placeholder="Enter a GitHub username"
          />
          <button
            (click)="searchGithubUsers()"
            class="flex-none rounded-md text-[#F8F8FF] bg-purple-700 px-3.5 py-2.5 text-sm font-semibold shadow-sm hover:bg-purple-500"
          >
            Search
          </button>
        </div>
      </div>

    </div>
    <!-- Profile Component -->
    <app-profile
      class="w-full"
      [profileData]="profileData"
      *ngIf="!loading && showProfile"
    />
    <!-- Loader Component -->
    <app-loader class="w-full" *ngIf="loading" />
  </div>

</div>
